<script>
    // Hàm để lấy sản phẩm dựa trên loại và trang hiện tại
    function filterProducts(page = 1) {
        const selectedCategory = document.getElementById('categoryFilter').value; // Lấy loại s");
            WriteLiteral(@"ản phẩm được chọn

        // Gọi API hoặc AJAX để lấy dữ liệu sản phẩm tương ứng với loại và trang
        fetch(`/api/products?category=${selectedCategory}&page=${page}`)
            .then(response => response.json())
            .then(data => {
                // Hiển thị danh sách sản phẩm
                renderProductList(data.products);

                // Hiển thị phân trang
                renderPagination(data.totalPages, page);
            })
            .catch(error => console.error('Error:', error));
    }

    // Hàm để hiển thị danh sách sản phẩm
    function renderProductList(products) {
        const productList = document.getElementById('productList');
        productList.innerHTML = ''; // Xóa danh sách hiện tại

        products.forEach(product => {
            const productItem = `
                <div class=""product-item"">
                    <h5>${product.name}</h5>
                    <p>Price: ${product.price}</p>
                    <a href=""/details/${product");
            WriteLiteral(@".id}"" class=""btn btn-primary"">Detail</a>
                    <a href=""/edit/${product.id}"" class=""btn btn-warning"">Edit</a>
                    <button class=""btn btn-danger"" onclick=""deleteItem(${product.id})"">Delete</button>
                </div>
            `;
            productList.innerHTML += productItem;
        });
    }

    // Hàm hiển thị phân trang
    function renderPagination(totalPages, currentPage) {
        const pagination = document.getElementById('pagination');
        pagination.innerHTML = ''; // Xóa phân trang hiện tại

        for (let i = 1; i <= totalPages; i++) {
            const pageItem = `
                <button onclick=""filterProducts(${i})"" class=""btn ${i === currentPage ? 'btn-primary' : 'btn-outline-primary'}"">
                    ${i}
                </button>`;
            pagination.innerHTML += pageItem;
        }
    }

    // Hàm để xử lý xóa sản phẩm
    function deleteItem(productId) {
        if (confirm(""Bạn có chắc chắn muốn xóa sản phẩ");
            WriteLiteral(@"m này không?"")) {
            // Gửi yêu cầu xóa sản phẩm qua API
            fetch(`/api/products/delete/${productId}`, {
                method: 'DELETE',
            })
                .then(response => {
                    if (response.ok) {
                        // Xóa thành công, cập nhật danh sách sản phẩm
                        filterProducts();
                    } else {
                        console.error('Error deleting product');
                    }
                });
        }
    }

    // Gọi lần đầu để hiển thị toàn bộ sản phẩm
    filterProducts();

